import React from 'react'

const Box = (props) => {
  let result
  if(
    props.title === "Computer" &&
    props.result !== "tie" &&
    props.result !== ""
  ) {// 카드가 computer 카드인가? && 결과가 비긴 건 아닌가? && props.result에 값이 있는가?}
    result = props.result === "win" ? "lose" : "win"
  }else {// 위의 경우가 아니라면 props로 전달된 결과를 그대로 쓴다
    result = props.result
  }
  if(props.title === "Computer") {
    console.log("computer", result)
  }

  
  return (
    <div className='box'>
        <h1>{props.title}</h1>
        <img className='item-img'src={props.item && props.item.img}></img>
        <h2>{props.result}</h2>
    </div>
  )
}

export default Box
