import logo from "./logo.svg";
import "./App.css";
import Box from "./component/Box";

// 1. 유저는 박스 2개를 볼 수 있다(타이틀, 사진, 결과)
// 2. 유저는 박스 하단에 가위바위보 버튼을 볼 수 있다
// 3. 버튼을 클릭하면 클릭한 아이템이 유저 박스에 보인다
// 4. 버튼을 클릭하면 컴퓨터 아이템은 랜덤하게 선택 된다
// 5. 3번, 4번의 아이템을 가지고 누가 이겼는지 승패를 나눈다
// 6. 승패 결과에 따라 테두리 색이 바뀐다. 지면 빨간색, 이기면 초록색, 비기면 검은색으로 변한다

// 사진과 이름을 가지고 있는 객체
const choice = {
  rock:{},
  scissors:{},
  paper:{}
}

function App() {
  return (
    <div>
      <div className="main">
        <Box title="You"></Box>
        <Box title="Computer"></Box>
      </div>

      <div className="main">
        <button>가위</button>
        <button>바위</button>
        <button>보</button>
      </div>
    </div>
  );
}

export default App;
